import Page from "../templates/Page";
import Link from "next/link";
import Timer from "../components/Timer";
import MileCounter from "../components/MileCounter";
import { useState, useEffect, Dispatch, SetStateAction } from 'react';




const getCoordinates: (arr: Coordinates[], callback: any) => void = (arr, callback) => {

    let response = null;
    var options = {
        enableHighAccuracy: true,
        timeout: 1000,
        maximumAge: 0
    };

    function success(pos: any) {
        let crd = pos.coords;
        response = {
            lat: crd.latitude,
            lng: crd.longitude,
            // utc: Date.now()
        } as Coordinates
        callback([...arr, response]);
        return;
    }

    function error(err: any) {
        console.warn(`ERROR(${err.code}): ${err.message}`);
    }

    navigator.geolocation.getCurrentPosition(success, error, options);
}

interface Coordinates {
    lat: number;
    lng: number;
    utc?: number;
}

interface StartStopProps {
    isRunning: boolean,
    toggle: Dispatch<SetStateAction<boolean>>
}

const StartStop = (props: StartStopProps) => {
    const { isRunning, toggle } = props;
    return (
        isRunning 
            ? <a onClick={() => toggle(false)}>Pause</a>
            : <a onClick={() => toggle(true)}>Start</a>
    )
}
const Running = () => {
    const [coordinates, setCoordinates] = useState([] as string[]);
    const [isRunning, setIsRunning] = useState(true);

    useEffect(() => {
        if(isRunning) {
            setInterval(() => {
                // const currentPosition = getCoordinates(coordinates, setCoordinates);
                setCoordinates([...coordinates, "a"]);

                console.log(coordinates)

                //console.log(coordinates);

            }, 1000)
        } 
    })



    return (
        <Page>
            <h1>Run!</h1>
            <MileCounter />
            <Timer/>
            <StartStop isRunning={isRunning} toggle={setIsRunning}/>
            <Link href="/finished"><a>Stop</a></Link>
        </Page>
    )
}

export default Running
