import Page from "../templates/Page";
import Link from "next/link";
import Timer from "../components/Timer";
import MileCounter from "../components/MileCounter";
import { useState, useEffect, Dispatch, SetStateAction } from 'react';



  function success(pos: any) {
    var crd = pos.coords;

    console.log('Your current position is:');
    console.log(`Latitude : ${crd.latitude}`);
    console.log(`Longitude: ${crd.longitude}`);
    console.log(`More or less ${crd.accuracy} meters.`);
  }

  function error(err: any) {
    console.warn(`ERROR(${err.code}): ${err.message}`);
  }



const getCoordinates: () => Coordinates | null = () => {
    var options = {
        enableHighAccuracy: true,
        timeout: 5000,
        maximumAge: 0
    };
    function success(pos: any) {
        let crd = pos.coords;

        console.log('Your current position is:');
        console.log(`Latitude : ${crd.latitude}`);
        console.log(`Longitude: ${crd.longitude}`);
        console.log(`More or less ${crd.accuracy} meters.`);
        return {
            lat: crd.latitude,
            lng: crd.longitude
        } as Coordinates
    }

    function error(err: any) {
        console.warn(`ERROR(${err.code}): ${err.message}`);
    }

    navigator.geolocation.getCurrentPosition(success, error, options);
    return null;

}

interface Coordinates {
    lat: number;
    lng: number;
    utc?: number;
}

interface StartStopProps {
    isRunning: boolean,
    toggle: Dispatch<SetStateAction<boolean>>
}

const StartStop = (props: StartStopProps) => {
    const { isRunning, toggle } = props;
    return (
        isRunning 
            ? <a onClick={() => toggle(false)}>Pause</a>
            : <a onClick={() => toggle(true)}>Start</a>
    )
}
const Running = () => {
    const [coordinates, setCoordinates] = useState([] as Coordinates[]);
    const [isRunning, setIsRunning] = useState(true);

    useEffect(() => {
        if(isRunning) {
            setInterval(() => {
                const currentPosition = getCoordinates();
                if(currentPosition !== null) {
                    setCoordinates([...coordinates, currentPosition])
                }
            }, 1000)
        } 
    })



    return (
        <Page>
            <h1>Run!</h1>
            <MileCounter />
            <Timer/>
            <StartStop isRunning={isRunning} toggle={setIsRunning}/>
            <Link href="/finished"><a>Stop</a></Link>
        </Page>
    )
}

export default Running
